import { Component, OnInit } from '@angular/core';
import { AuthService } from '../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {
  username = "";
  password = "";
  errorMsg = ""

  constructor(private auth: AuthService, private router: Router) { }

  ngOnInit(): void {

  }

  login(){
    if(this.username.trim().length === 0){
      this.errorMsg="Username is required";
    }
    else if(this.password.trim().length === 0){
      this.errorMsg="Password is required";
    }
    else{
      this.errorMsg="";
      let res = this.auth.login(this.username,this.password);
      if(res === 200){
        this.router.navigate(['home']);
      }
      if (res === 403){
        this.errorMsg = "Invalid Credentials";
      }
    }
  }
}
<div class="login-container">
<img class="film" src="assets/Images/illustration-industry-health.png" alt="">
<div class="logo">Altera Healthcare</div>
<div class="desc">A global healthcare IT innovator, Altera Digital Health develops and elevates technology to connect and 
    inspire healthier communities. Our platform approach to our solutions is changing the way healthcare is delivered. 
    By keeping the human user at the center of our design methodology, 
    we are supporting caregivers and helping patients meet and exceed their wellness goals. Together, with our clients, 
    we’re driving a new era of healthcare.</div>

    <div class="login-form">
        <div class="form-group">
            <label for="uname">Username</label>
            <input type="text" name="uname" class="form-control text1" [(ngModel)]="username"/>
        </div>
        <div class="form-group">
            <label for="pword">Password</label>
            <input type="password" name="pword" class="form-control text1" [(ngModel)]="password"/>
        </div>

        <div class="btn-container">
            <div class="error">{{errorMsg}}</div>
            <button class="btn btn-dark" (click)="login()">Login</button>
        </div>
    </div>

</div>
